mapper.select:
WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT a.announcement_id, a.title, e.name, a.created_at FROM announcement a JOIN employee e ON a.emp_id = e.emp_id
a.is_deleted = 'N'
AND a.title LIKE CONCAT('%', #{title}, '%')
AND e.name LIKE CONCAT('%', #{name}, '%')
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND a.created_at >= #{startDate}
AND a.created_at <= #{endDate}
ORDER BY a.created_at ${sortDirection} LIMIT #{limit} OFFSET #{offset}
