mapper.select:
SELECT r.round_id AS roundId, r.round_no AS roundNo, r.start_at AS startAt, DATE_ADD(r.start_at, INTERVAL 7 DAY) AS endAt, ( SELECT COUNT(DISTINCT er.eval_id) FROM eval_response er WHERE er.round_id = r.round_id ) AS participantCount, CASE WHEN CURRENT_DATE < r.start_at THEN 'BEFORE' WHEN CURRENT_DATE > DATE_ADD(r.start_at, INTERVAL 7 DAY) THEN 'DONE' ELSE 'IN_PROGRESS' END AS status FROM eval_round r WHERE 1 = 1
AND r.start_at >= #{startDate}
AND r.start_at <= #{endDate}
ORDER BY r.start_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM eval_round r
WHERE 1 = 1

        AND r.start_at >= #{startDate}


        AND r.start_at <= #{endDate}
