mapper.select:
WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT r.round_no AS roundNo, ef.description AS formName, t.emp_no AS targetEmpNo, t.name AS targetName, d.name AS departmentName, p.name AS positionName, e.emp_no AS evaluatorEmpNo, e.name AS evaluatorName, er.score AS score, DATE_FORMAT(er.created_at, '%Y-%m-%d %H:%i') AS submittedAt FROM eval_response er JOIN employee t ON er.target_id = t.emp_id JOIN employee e ON er.eval_id = e.emp_id JOIN eval_form ef ON ef.form_id = er.form_id JOIN eval_type et ON ef.type_id = et.type_id LEFT JOIN department d ON t.dept_id = d.dept_id LEFT JOIN position p ON t.position_id = p.position_id JOIN eval_round r ON er.round_id = r.round_id
AND r.round_id = #{roundId}
AND t.emp_no = #{targetEmpNo}
AND e.emp_no = #{evaluatorEmpNo}
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND t.position_id = #{positionId}
ORDER BY r.round_no DESC, t.emp_no, e.emp_no LIMIT 5000

WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT r.round_no AS roundNo, f.description AS formName, e.emp_no AS evalEmpNo, e.name AS evalName, d.name AS departmentName, p.name AS positionName, er.score AS score, DATE_FORMAT(er.created_at, '%Y-%m-%d %H:%i') AS submittedAt FROM eval_response er JOIN employee e ON er.eval_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN eval_round r ON er.round_id = r.round_id JOIN eval_form f ON er.form_id = f.form_id JOIN eval_type et ON f.type_id = et.type_id
AND et.name = 'ORG'
AND r.round_id = #{roundId}
AND e.emp_no = #{evalEmpNo}
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND e.position_id = #{positionId}
ORDER BY r.round_no DESC, e.emp_no LIMIT 5000

WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT r.round_no AS roundNo, f.description AS formName, e.emp_no AS empNo, e.name AS name, d.name AS departmentName, p.name AS positionName, er.score AS score, DATE_FORMAT(er.created_at, '%Y-%m-%d %H:%i') AS submittedAt FROM eval_response er JOIN employee e ON er.eval_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN eval_round r ON er.round_id = r.round_id JOIN eval_form f ON er.form_id = f.form_id JOIN eval_type et ON f.type_id = et.type_id
AND et.name = 'SELF'
AND r.round_id = #{roundId}
AND e.emp_no = #{empNo}
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND e.position_id = #{positionId}
ORDER BY r.round_no DESC, e.emp_no LIMIT 5000
