mapper.select:
SELECT o.objection_id AS objectionId, e_target.emp_no AS empNo, e_target.name AS employeeName, r.round_no AS roundNo, DATE_FORMAT(o.created_at, '%Y-%m-%d %H:%i:%s') AS createdAt, s.status_type AS status, er.score AS score, o.reason AS reason FROM hr_objection o JOIN eval_response er ON er.result_id = o.result_id JOIN employee e_target ON e_target.emp_id = er.target_id JOIN eval_round r ON r.round_id = er.round_id JOIN status s ON s.status_id = o.status_id
AND er.eval_id = #{requesterEmpId}
AND o.status_id = #{statusId}
AND r.round_no = #{roundNo}
AND DATE(o.created_at) >= #{startDate}
AND DATE(o.created_at) <= #{endDate}
ORDER BY o.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM hr_objection o
JOIN eval_response er ON er.result_id = o.result_id

    AND er.eval_id = #{requesterEmpId}


        AND o.status_id = #{statusId}


        AND EXISTS (
        SELECT 1 FROM eval_round r
        WHERE r.round_id = er.round_id
        AND r.round_no = #{roundNo}
        )


        AND DATE(o.created_at) >= #{startDate}


        AND DATE(o.created_at) <= #{endDate}
