mapper.select:
SELECT e.emp_no AS employeeNo, e.name AS employeeName, d.name AS departmentName, p.name AS positionName, k.goal AS goal, k.goal_value AS goalValue, k.kpi_progress AS kpiProgress, s.status_type AS statusName, DATE_FORMAT(k.created_at, '%Y-%m-%d') AS createdAt, DATE_FORMAT(k.deadline, '%Y-%m-%d') AS deadline FROM kpi k JOIN employee e ON k.emp_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN status s ON k.status_id = s.status_id
k.is_deleted = 'N'
AND e.emp_no = #{empNo}
AND e.dept_id = #{deptId}
AND e.position_id = #{positionId}
AND k.status_id = #{statusId}
AND DATE(k.created_at) >= #{startDate}
AND DATE(k.created_at) <= #{endDate}
ORDER BY k.created_at DESC LIMIT 5000
