mapper.select:
WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT k.kpi_id, e.emp_no, e.name AS employeeName, d.name AS departmentName, p.name AS positionName, k.goal, k.goal_value, k.kpi_progress, s.status_type AS statusName, DATE_FORMAT(k.created_at, '%Y-%m-%d') AS createdAt, DATE_FORMAT(k.deadline, '%Y-%m-%d') AS deadline, k.is_deleted AS isDeleted FROM kpi k JOIN employee e ON k.emp_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN status s ON k.status_id = s.status_id
AND e.emp_no = #{empNo}
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND e.position_id = #{positionId}
AND k.status_id = #{statusId}
AND k.created_at BETWEEN #{startDate} AND #{endDate}
AND k.is_deleted = #{isDeleted}
ORDER BY k.created_at DESC LIMIT #{size} OFFSET #{getOffset}

WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT COUNT(*) FROM kpi k JOIN employee e ON k.emp_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN status s ON k.status_id = s.status_id
k.is_deleted = 'N'
AND e.emp_no = #{empNo}
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND e.position_id = #{positionId}
AND k.status_id = #{statusId}
AND k.created_at BETWEEN #{startDate} AND #{endDate}
AND k.is_deleted = #{isDeleted}

WITH RECURSIVE dept_tree AS ( SELECT dept_id FROM department WHERE dept_id = #{deptId} AND is_deleted = 'N' UNION ALL SELECT d.dept_id FROM department d INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id WHERE d.is_deleted = 'N' ) SELECT e.emp_no, e.name AS employeeName, d.name AS departmentName, p.name AS positionName, COUNT(k.kpi_id) AS totalKpiCount, SUM(CASE WHEN s.status_type = 'ACCEPTED' THEN 1 ELSE 0 END) AS completedKpiCount, ROUND(AVG(k.kpi_progress), 1) AS averageProgress, ROUND(SUM(CASE WHEN k.kpi_progress >= 100 THEN 1 ELSE 0 END) * 100.0 / COUNT(k.kpi_id), 1) AS completionRate FROM kpi k JOIN employee e ON k.emp_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN status s ON k.status_id = s.status_id WHERE k.is_deleted = 'N'
AND e.emp_no = #{empNo}
AND e.dept_id IN (SELECT dept_id FROM dept_tree)
AND e.position_id = #{positionId}
AND YEAR(k.created_at) = #{year}
AND MONTH(k.created_at) = #{month}
GROUP BY e.emp_id ORDER BY averageProgress DESC LIMIT #{size} OFFSET #{getOffset}

WITH RECURSIVE dept_tree AS (
    SELECT dept_id
    FROM department
    WHERE dept_id = #{deptId}
        AND is_deleted = 'N'
    UNION ALL
    SELECT d.dept_id
    FROM department d
    INNER JOIN dept_tree dt ON d.parent_dept_id = dt.dept_id
    WHERE d.is_deleted = 'N'
)
SELECT COUNT(*) FROM (
SELECT e.emp_id
FROM kpi k
JOIN employee e ON k.emp_id = e.emp_id
WHERE k.is_deleted = 'N'

        AND e.emp_no = #{empNo}


        AND e.dept_id IN (SELECT dept_id FROM dept_tree)


        AND e.position_id = #{positionId}


        AND YEAR(k.created_at) = #{year}


        AND MONTH(k.created_at) = #{month}

GROUP BY e.emp_id
) AS summary
