mapper.select:
SELECT k.kpi_id, e.emp_no, e.name AS employeeName, d.name AS departmentName, p.name AS positionName, k.goal, k.goal_value, k.kpi_progress, s.status_type AS statusName, DATE_FORMAT(k.created_at, '%Y-%m-%d') AS createdAt, DATE_FORMAT(k.deadline, '%Y-%m-%d') AS deadline FROM kpi k JOIN employee e ON k.emp_id = e.emp_id LEFT JOIN department d ON e.dept_id = d.dept_id LEFT JOIN position p ON e.position_id = p.position_id JOIN status s ON k.status_id = s.status_id
k.is_deleted = 'N'
AND e.emp_no = #{empNo}
AND e.dept_id = #{deptId}
AND e.position_id = #{positionId}
AND k.status_id = #{statusId}
AND k.created_at BETWEEN #{startDate} AND #{endDate}
ORDER BY k.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM kpi k
JOIN employee e ON k.emp_id = e.emp_id
LEFT JOIN department d ON e.dept_id = d.dept_id
LEFT JOIN position p ON e.position_id = p.position_id
JOIN status s ON k.status_id = s.status_id

    k.is_deleted = 'N'

        AND e.emp_no = #{empNo}


        AND e.dept_id = #{deptId}


        AND e.position_id = #{positionId}


        AND k.status_id = #{statusId}


        AND k.created_at BETWEEN #{startDate} AND #{endDate}
