mapper.select:
SELECT
    COUNT(*) AS totalKpiCount,
    SUM(CASE WHEN k.kpi_progress = 100 THEN 1 ELSE 0 END) AS completedKpiCount,
    ROUND(AVG(k.kpi_progress), 2) AS averageProgress
FROM kpi k
JOIN employee e ON k.emp_id = e.emp_id

    k.is_deleted = 'N'
    AND k.status_id = 2

        AND YEAR(k.created_at) = #{year}
        AND MONTH(k.created_at) = #{month}


        AND e.dept_id = #{deptId}


        AND k.emp_No = #{empNo}


        AND e.position_id = #{positionId}

SELECT MONTH(k.created_at) AS month, COUNT(*) AS totalKpiCount, SUM(CASE WHEN k.kpi_progress = 100 THEN 1 ELSE 0 END) AS completedKpiCount, ROUND(AVG(k.kpi_progress), 2) AS averageProgress FROM kpi k JOIN employee e ON k.emp_id = e.emp_id WHERE k.is_deleted = 'N' AND k.status_id = 2 AND YEAR(k.created_at) = #{year}
AND emp_no = #{empNo}
AND e.dept_id = #{deptId}
AND e.position_id = #{positionId}
GROUP BY MONTH(k.created_at) ORDER BY month
