mapper.select:
SELECT er.result_id AS resultId, ef.name AS formName, r.round_no AS roundNo, er.score AS score, er.created_at AS createdAt FROM eval_response er JOIN eval_form ef ON er.form_id = ef.form_id JOIN eval_type et ON ef.type_id = et.type_id JOIN eval_round r ON er.round_id = r.round_id WHERE et.name = 'ORG'
AND er.form_id = #{formId}
AND er.round_id = #{roundId}
ORDER BY er.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM eval_response er JOIN eval_form ef ON er.form_id = ef.form_id JOIN eval_type et ON ef.type_id = et.type_id WHERE et.name = 'ORG'
AND er.form_id = #{formId}
AND er.round_id = #{roundId}
