mapper.select:
SELECT er.result_id AS resultId, er.eval_id AS evalId, e1.name AS evalName, er.target_id AS targetId, e2.name AS targetName, ef.name AS formName, er.score AS score, r.round_no AS roundNo, er.reason AS reason, er.created_at AS createdAt FROM eval_response er JOIN employee e1 ON er.eval_id = e1.emp_id JOIN employee e2 ON er.target_id = e2.emp_id JOIN eval_form ef ON er.form_id = ef.form_id JOIN eval_type et ON ef.type_id = et.type_id JOIN eval_round r ON er.round_id = r.round_id WHERE et.name = 'PEER'
AND er.eval_id = #{evalId}
AND er.target_id = #{targetId}
AND er.form_id = #{formId}
AND er.round_id = #{roundId}
ORDER BY er.created_at DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM eval_response er
JOIN eval_form ef ON er.form_id = ef.form_id
JOIN eval_type et ON ef.type_id = et.type_id
WHERE et.name = 'PEER'

        AND er.eval_id = #{evalId}


        AND er.target_id = #{targetId}


        AND er.form_id = #{formId}


        AND er.round_id = #{roundId}
