mapper.select:
SELECT con.contract_id, con.emp_id, emp.emp_no, emp.name AS empName, con.type, con.salary, con.created_at, file.attachment_id, file.s3_url FROM contract AS con JOIN employee AS emp ON con.emp_id = emp.emp_id JOIN file AS file ON con.contract_id = file.contract_id WHERE 1 = 1
AND emp.emp_no = #{request.empNo}
AND emp.name = #{request.empName}
AND con.type = #{request.type}
AND con.created_at >= #{request.searchStartDate}
AND con.created_at < #{request.searchEndDate}
ORDER BY con.created_at
DESC
LIMIT #{request.limit} OFFSET #{request.offset}

SELECT
    COUNT(con.contract_id)
FROM contract AS con
JOIN employee AS emp ON con.emp_id = emp.emp_id
JOIN file AS file ON con.contract_id = file.contract_id
WHERE 1 = 1

    AND emp.emp_no = #{request.empNo}


    AND emp.name = #{request.empName}


    AND con.type = #{request.type}


    AND con.created_at >= #{request.searchStartDate}


    AND con.created_at < #{request.searchEndDate}

SELECT con.contract_id, con.emp_id, con.type, con.salary, con.created_at, file.attachment_id, file.s3_url FROM contract AS con JOIN employee AS emp ON con.emp_id = emp.emp_id JOIN file AS file ON con.contract_id = file.contract_id WHERE con.emp_id = #{empId}
AND con.type = #{request.type}
ORDER BY con.created_at DESC LIMIT #{request.limit} OFFSET #{request.offset}
