mapper.select:
SELECT r.round_id AS roundId, r.round_no AS roundNo, r.year, r.month, CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01') AS startDate, LAST_DAY(CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01')) AS endDate, ( SELECT COUNT(DISTINCT rs.emp_id) FROM retention_support rs WHERE rs.round_id = r.round_id ) AS participantCount FROM retention_round r
AND ( CASE WHEN CURRENT_DATE < CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01') THEN 'PLANNED' WHEN CURRENT_DATE BETWEEN CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01') AND LAST_DAY(CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01')) THEN 'IN_PROGRESS' ELSE 'COMPLETED' END = #{req.status} )
AND CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01') >= #{req.startDate}
AND LAST_DAY(CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01')) <= #{req.endDate}
GROUP BY r.round_id, r.round_no, r.year, r.month ORDER BY r.round_no DESC LIMIT #{req.size} OFFSET #{req.getOffset}

SELECT COUNT(*)
FROM retention_round r


        AND (
        CASE
            WHEN CURRENT_DATE < CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01') THEN 'PLANNED'
            WHEN CURRENT_DATE BETWEEN CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01')
            AND LAST_DAY(CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01')) THEN 'IN_PROGRESS'
            ELSE 'COMPLETED'
        END = #{req.status}
        )


        AND CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01') >= #{req.startDate}


        AND LAST_DAY(CONCAT(r.year, '-', LPAD(r.month, 2, '0'), '-01')) <= #{req.endDate}
