mapper.select:
SELECT work.work_id, work.emp_id, emp.emp_no, emp.name AS empName, work.type_id, wt.type_name, work.vacation_type_id, vt.vacation_type AS vacationType, work.start_at, work.end_at, work.is_normal_work FROM work AS work JOIN employee AS emp ON work.emp_id = emp.emp_id JOIN work_type AS wt ON work.type_id = wt.type_id LEFT JOIN vacation_type AS vt ON work.vacation_type_id = vt.vacation_type_id WHERE work.emp_id = #{empId}
AND work.start_at >= #{request.rangeStartDate}
AND work.start_at <= #{request.rangeEndDate}
ORDER BY work.start_at ASC;

SELECT work.work_id, work.emp_id, emp.emp_no, emp.name AS empName, work.type_id, wt.type_name, work.vacation_type_id, vt.vacation_type AS vacationType, work.start_at, work.end_at, work.is_normal_work FROM work AS work JOIN employee AS emp ON work.emp_id = emp.emp_id JOIN work_type AS wt ON work.type_id = wt.type_id LEFT JOIN vacation_type AS vt ON work.vacation_type_id = vt.vacation_type_id WHERE 1 = 1
AND emp.emp_no = #{request.empNo}
AND emp.name = #{request.empName}
AND work.start_at >= #{request.rangeStartDate}
AND work.start_at <= #{request.rangeEndDate}
AND wt.type_name = #{request.typeName}
ORDER BY work.start_at ASC
ORDER BY work.start_at DESC
LIMIT #{request.limit} OFFSET #{request.offset};

SELECT
COUNT(work.work_id)
FROM work AS work
JOIN employee AS emp ON work.emp_id = emp.emp_id
JOIN work_type AS wt ON work.type_id = wt.type_id
LEFT JOIN vacation_type AS vt ON work.vacation_type_id = vt.vacation_type_id
WHERE 1 = 1

    AND emp.emp_no = #{request.empNo}


    AND emp.name = #{request.empName}


    AND work.start_at  >=  #{request.rangeStartDate}


    AND work.start_at  <=  #{request.rangeEndDate}


    AND wt.type_name = #{request.typeName}

;

SELECT
    'Y' AS isAttended,
    work.work_id,
    work.emp_id
FROM work AS work
WHERE work.emp_id = #{empId}
AND work.start_at >= #{today}
AND work.start_at < #{tomorrow}

UNION ALL

SELECT
    'N' AS isAttended,
    null AS workId,
    null AS empId
FROM DUAL
WHERE NOT EXISTS (
    SELECT 1
    FROM work
    WHERE emp_id = #{empId}
    AND work.start_at >= #{today}
    AND work.start_at < #{tomorrow}
)
LIMIT 1
